import React, {useState}from "react";
import "./Login.css"

const Login =() =>{
  
  const [user,setUser] = useState({
    email : '',
    password : ''

});

const handleInput = (event) =>{
    let name = event.target.name
    let value = event.target.value
    
    setUser({...user,[name]:value})
}


const handleSubmit = async(event) =>{
    event.preventDefault();
    const{email, password} = user;
    try{
        const res = await fetch('/login',{
            method : "POST",
            headers : {
                "Content-Type" : "application/json"
            },
            body : JSON.stringify({
                email,password
            })
        });
        if(res.status === 400 || !res ){
            window.alert("Invalid Credentials")
        }else{
            window.alert("Login Successfull");
            window.location.reload();
        }
    } catch(error){
        console.log(error);

    }

}
 
  
    return(
       
      <div className="div">
        <div className="div-2">
          <div className="column">
            <div className="div-3">
              <div className="div-4">LOG IN</div>
              <form onSubmit={handleSubmit}>
              <div className="div-5">
              
              </div>
              <div className="div-6">
              <label htmlFor="exampleInputUserName" className="form label">User Name</label>
              <input type="email" className="form-control" id="exampleInputUserName" aria-describedby="emailHelp" 
                                name = "username"
                               value = {user.username}
                               onChange={handleInput}/>
             </div>
              <div className="div-7">
              </div>
              <div className="div-8">Enter Password</div>
              
              <div className="div-9">
                <div className="div-10">Remember me</div>
                <div className="div-11">
                  Forget Password<span style={{ color: "rgba(3,4,94,1)" }}>?</span>
                </div>
                
              </div>
              </form>
              <div className="div-12">Log in</div>
              <div className="div-13">
                <div className="div-14" />
                <div className="div-15">Or</div>
                <div className="div-16" />
              </div>
              <div className="div-17">
                <div className="div-18">Don’t have an account?</div>
                <div className="div-19">Click here</div>
              </div>
            </div>
          </div>
          <div className="column-2">
            <img loading="lazy" srcSet="..." className="img" alt="Some meaningful description" />
          </div>
        </div>
      </div>
   
    )
}

export default Login;
